import fs from "fs/promises";
export const readAndWrite = async (FILE) => {
 try{
 const data = await fs.readFile(FILE, "utf-8");
 return JSON.parse(data);
    }catch(err){
        console.log(err);
    }
};
export const write = async (FILE, data) => {
    let messsage=" ";
    let status= 0;
    try{
        await fs.writeFile(FILE, JSON.stringify(data));
        messsage="Data has been written successfully";
        status= 200;
    }catch(err){
        message="unable to write data";
        status= 500;
    }
    return {message,status};
};
